function createEngineerAway()
	local angle = createDirection + 270
	if angle > 360 then 
		angle = angle - 360
	end
	local sox, soy = vectorFromAngle(angle,createDistance*1000)
	engineerCreation(nearx, neary, sox, soy)
end
