	if selected_mine_blob ~= nil then
		selected_mine_blob.show_clock = getScenarioTime()
		if selected_mine_blob[1]:isValid() then
			addGMMessage(string.format("Mine blob in sector %s",selected_mine_blob[1]:getSectorName()))
		end
	else
