		update = function (self, obj, delta)
			local upper_heat = 0.98
			local heat = extraMath.clamp(obj:getSystemHeat("beamweapons"),0,upper_heat)
			heat = heat/upper_heat
			--					Arc,  Dir, Range,		  Cycle Time, Dmg
