		gm_click_mode = "zone rectangle"
		onGMClick(gmClickZoneRectangle)
		if prev_mode ~= nil then
			addGMMessage(string.format("Cancelled current GM Click mode\n   %s\nIn favor of\n   zone rectangle\nGM click mode.",prev_mode))
		end
	end
	addZoneByClick()
